(500837, 590, ' Sacco despede-se de seu filho.', CAST(0 AS BOOLEAN), now(), now()),
(500838, 590, ' Os comdemnados são conduzidos, novamente, para o cubiculo funebre.', CAST(0 AS BOOLEAN), now(), now()),
(500839, 590, ' Todos os braços cáem, no desanimo extremo.', CAST(0 AS BOOLEAN), now(), now()),
(500840, 590, '  Desfazem-se as ultimas claridades.', CAST(0 AS BOOLEAN), now(), now()),
(500841, 590, ' A tragedia vae entrar no seu ultimo acto.', CAST(0 AS BOOLEAN), now(), now()),
(500842, 590, ' Já os machinistas se preparam para correr o velario.', CAST(0 AS BOOLEAN), now(), now()),
