INSERT INTO contextos (id, obra_id, conteudo, fake, created_at, updated_at) VALUES
(503646, 665, '  Não, não é possivel; mas se formos procurar e indagar a fonte d''onde o homem bebe tanta força e firmeza, veremos que essa fonte não é outra cousa mais do que o estudo, sempre inexgotavel.', CAST(0 AS BOOLEAN), now(), now()),
(503647, 665, '   Sim, pois foi a custo do estudo, a custo de uma vida consummida sobre os livros, que homens que vivem ainda hoje e sempre viverão na imaginação da posteridade, legaram-nos o fructo de seus trabalhos e adquiriram um nome glorioso, um nome immortal.', CAST(0 AS BOOLEAN), now(), now()),
(503648, 665, '  Não foi no ocio, no ruido das festas, no rumor das salas, que elles conquistaram esse nome; mas sim procurando por todos os meios desenvolver as suas intelligencias.', CAST(0 AS BOOLEAN), now(), now()),
(503649, 665, ' O caminho que trilharam, também não foi um caminho limpo e livre de tropeços; mas, ao contrario, frequentes obstaculos se lhes antolhavam; elles, porém, com a constancia que soem ter esses homens, não desanimavam e iam buscar nos livros nova força para vencer e destruir os obstaculos.', CAST(0 AS BOOLEAN), now(), now()),
(503650, 665, '  Já consideramos ligeiramente o bem que do estudo resulta para cada nação, para a humanidade e para o mundo inteiro.', CAST(0 AS BOOLEAN), now(), now()),
(503651, 665, ' Vejamos agora os bens que do estudo resultam para para cada um em particular.', CAST(0 AS BOOLEAN), now(), now()),
(503652, 665, '  Poderiamos comparar a nossa intelligencia á um diamante; antes de lapidado, ao vermo​l<i>-</i>o, á custo o distinguiriamos de uma outra pedra, tal é o seu estado: nenhum attractivo, nenhuma causa de admiração n''ella encontramos; mas eis que o lapidario a toma e a trata; então pouco a pouco vae mostrando o seu brilho e agradando a todos que a vêem.', CAST(0 AS BOOLEAN), now(), now()),
(503653, 665, '  Tal é a nossa intelligencia.', CAST(0 AS BOOLEAN), now(), now()),
(503654, 665, '  A principio não passa de uma pedra bruta, que não tem nada de admiravel; mas pouco a pouco, também a custa d''esse lapidario - o estudo, ella vae sahindo d''aquella casca que a encobria, tornando-se mais bella, e como o brilhante lançando também seus raios, não raios physicos, mais outros mais bellos - que são luzes da intelligencia.', CAST(0 AS BOOLEAN), now(), now()),
(503655, 665, '   Eis o que o estudo opera em cada um de nós.', CAST(0 AS BOOLEAN), now(), now()),
(503656, 665, '   À vista dos bens que o estudo produz, quem hesitaria cultiva​l<i>-</i>o com mais fervor?', CAST(0 AS BOOLEAN), now(), now()),
(503657, 665, '  Ninguem, certamente.', CAST(0 AS BOOLEAN), now(), now()),
(503658, 665, ' Não creio que vós, ó mancebos, em cujas mãos bem cedo estarão os destinos da patria, hesiteis em cultiva​l<i>-</i>o e ama​l<i>-</i>o com todo o fervor do vosso coração juvenil, pois é d''elle que depende a honra de vossa memoria, a felicidade de vossos filhos e a prosperidade de vossa patria.', CAST(0 AS BOOLEAN), now(), now()),
