(507886, 764, ' - Eu?', CAST(0 AS BOOLEAN), now(), now()),
(507887, 764, ' - Claro!', CAST(0 AS BOOLEAN), now(), now()),
(507888, 764, ' Porque é que tu não dás encima da Clarice?', CAST(0 AS BOOLEAN), now(), now()),
(507889, 764, ' - Pelo amor de Deus!', CAST(0 AS BOOLEAN), now(), now()),
(507890, 764, ' Você é maluco?', CAST(0 AS BOOLEAN), now(), now()),
(507891, 764, ' Imagine se o marido sabe?', CAST(0 AS BOOLEAN), now(), now()),
(507892, 764, ' Ela é casada, rapaz!', CAST(0 AS BOOLEAN), now(), now()),
(507893, 764, '  E Robertinho: - Vai no meu golpe, que eu aposto minha cabeça, não me engano nunca!', CAST(0 AS BOOLEAN), now(), now()),
(507894, 764, ' Queres apostar?', CAST(0 AS BOOLEAN), now(), now()),
(507895, 764, ' Quanto?', CAST(0 AS BOOLEAN), now(), now()),
(507896, 764, '  No fim, desorientado ante a insistência do outro, o Oliveira se refugiou detrás de um argumento patético:  - É honestíssima!', CAST(0 AS BOOLEAN), now(), now()),
(507897, 764, ' incapaz de trair o marido!', CAST(0 AS BOOLEAN), now(), now()),
(507898, 764, ' Ponho a minha mão no fogo!', CAST(0 AS BOOLEAN), now(), now()),
