(512085, 862, ' - Oh deixa de bobagem!', CAST(0 AS BOOLEAN), now(), now()),
(512086, 862, ' Morrer coisa nenhuma!', CAST(0 AS BOOLEAN), now(), now()),
(512087, 862, ' Parece criança!', CAST(0 AS BOOLEAN), now(), now()),
